package com.auium.driverimport com.auium.element.Selector.ClassChainimport com.auium.remote.CommandExecutionHelperimport com.auium.remote.DriverStatusimport com.auium.remote.MobileCommandimport com.auium.remote.convertimport kotlin.test.Testclass DriverTest {    private val url = "http://localhost:8100"    private val bundleId: String = "com.apple.TestFlight"    @Test    fun driverBuildTest() {        driver.locked()        driver.unlock()        driver.status()    }    @Test    fun sessionTest() {        driver.session(bundleId, "zh-tw")        driver.home()    }    @Test    fun launchAppTest() {        driver.launch(bundleId, "zh-hk")    }    @Test    fun terminateTest() {        driver.terminate(bundleId)    }    @Test    fun homeTest() {        driver.session(bundleId)        driver.close()    }    @Test    fun findElementTest() {        val el = driver.findElement(ClassChain("**/XCUIElementTypeImage[`name == \"basic_cart-24\"`]"))        println(el?.rect())        println(el?.size())        println(el?.location())        el?.tap()        Thread.sleep(1000)        driver.findElement(ClassChain("**/XCUIElementTypeButton[`name == 'Wishlist'`]"))?.tap()    }    @Test    fun findElementsTest() {        val els = driver.findElements(ClassChain("**/XCUIElementTypeTabBar/XCUIElementTypeButton"))        els.forEach {            println(it)        }    }    @Test    fun sendKeysTest() {        val input = driver.findElement(ClassChain("**/XCUIElementTypeTextField"))        input?.clear()        input?.sendKeys("YU")    }    @Test    fun getSourceTest() {        println(driver.source())    }    @Test    fun requestTest() {        val response = CommandExecutionHelper().execute(MobileCommand.STATUS)        val status = response.convert<DriverStatus>()        println(status)    }    @Test    fun getWindowSizeTest() {        println(driver.windowSize)    }}